int init_U(char* pes_file) {

	FILE* read;

	read = fopen(pes_file, "r");

	if(!(read = fopen(pes_file, "r"))){
        printf("There is no File with PES data --- % !!!\n", pes_file);
        return 1;
    }

	char strbuf[255];
	int i, j, pes_type = 0, *N_neuron, **n_link;
	double **w, **b;
	char ch;

	if (fscanf(read, "%lf %lf %lf", &mass[0], &mass[1], &mass[2]) != 3)
		return 2;

	if (fscanf(read, "%lf %lf %lf", &Ze[0], &Ze[1], &Ze[2]) != 3)
		return 2;

	if (fscanf(read, "%s %s %s", name_particle[0], name_particle[1], name_particle[2]) != 3)
		return 2;

	fscanf(read, "%s", strbuf);
	if (strcmp(strbuf, "PIP-NN") == 0)
		pes_type = 1;
	else
		return 2;

	if (fscanf(read, "%d", &Num_net) != 1)
		return 3;
	
	if (Num_net <= 0)
		return 3;

	net = new NNet[Num_net];
	
	N_neuron = (int*)calloc(Num_net, sizeof(int));
	n_link = (int**)malloc(Num_net * sizeof(int*));
	w = (double**)malloc(Num_net * sizeof(double*));
	b = (double**)malloc(Num_net * sizeof(double*));

	for(i=0; i<Num_net; i++){
		if (fscanf(read, "%d", &N_neuron[i]) != 1) // potential place for memory leak
			return 4;
		
		//printf("N_neuron=%d\n", N_neuron[i]);

		n_link[i] = (int*)malloc(N_neuron[i] * sizeof(int));

		for(j=0; j<N_neuron[i]; j++){
			if (fscanf(read, "%d", &n_link[i][j]) != 1) // potential place for memory leak
				return 4;
			//printf("%d ", n_link[i][j]);
		}
		
		int dim_w = 0, dim_b = 0;

		for(j=0; j<N_neuron[i]-1; j++){
			dim_w += n_link[i][j]*n_link[i][j+1];
			dim_b += n_link[i][j+1];
		}

		//printf("\ndim_w=%d dim_b=%d\n", dim_w, dim_b);

		w[i] = (double*)malloc(dim_w * sizeof(double));

		for(j=0; j< dim_w; j++){
			if (fscanf(read, "%lf", &w[i][j]) != 1) // potential place for memory leak
				return 4;
			//printf("%f ", w[i][j]);
		}

		b[i] = (double*)malloc(dim_b * sizeof(double));

		for(j=0; j<dim_b; j++){
			if (fscanf(read, "%lf", &b[i][j]) != 1) // potential place for memory leak
				return 4;
			//printf("%f ", b[i][j]);
		}

		net[i].init_net(N_neuron[i], n_link[i], w[i], b[i]);
	}

	for(i=0; i<Num_net; i++){
		free(n_link[i]);
		free(w[i]);
		free(b[i]);
	}

	free(N_neuron);
	free(n_link);
	free(w);
	free(b);

	fscanf(read, "%c", &ch);
	if (fscanf(read, "%c", &ch) == 1) {
		if (ch == '#') {
			if (fscanf(read, "%lf", &D_lim) != 1)
				return 4;
			if (fscanf(read, "%lf %lf %lf", &re_AB, &re_AC, &re_BC) != 3)
				return 4;
			if (fscanf(read, "%lf %lf %lf", &Te_AB, &Te_AC, &Te_BC) != 3)
				return 4;
		}
		else {
			return 4;
		}
	}
	else {
		U_properties();
	}

	fclose(read);

	return 0;
}

int U_properties() {

	double inp[3], r[3], U[3];

	for(int i=0; i<3; i++)
		inp[i] = 20.;

	D_lim = U_dist(inp);

	for(int j=0; j<3; j++){
		double U1, U2, U0, dr;
		for(int i=0; i<3; i++)
			inp[i] = 20.;
		inp[j] = 1.;

		for(int i=0; i<50; i++){
			
			U0 = U_dist(inp);

			inp[j] += 0.001;
			U2 = U_dist(inp);

			inp[j] -= 0.002;
			U1 = U_dist(inp);

			dr = (U2-U1)*0.5*0.001/(U2-2*U0+U1);

			if(fabs(dr>0.3))
				dr = dr/fabs(dr)*0.3;

			if(fabs(dr)<1e-8)
				break;
			else
				inp[j] -= dr*0.5;
		}

		r[j] = inp[j];
		U[j] = U0;

		switch(j){
			case 0:
				re_AB = inp[j];
				Te_AB = U0;
			break;
			case 1:
				re_AC = inp[j];
				Te_AC = U0;
			break;
			case 2:
				re_BC = inp[j];
				Te_BC = U0;
			break;
		}
		//printf("r=%.10f\tU=%.10f\n", inp[j], U0 * 8.064877E+3);
	}

	printf("\tPlease add this block to file, defining PES data:\n#\n%.10f\n%.10f %.10f %.10f\n%.10f %.10f %.10f", D_lim, r[0], r[1], r[2], U[0], U[1], U[2]);

	return 0;
}

int bound_states(double x1, double xN, int N, int J, int order, int r_type, int *N_levels) {

	double h, gamma, *a, *b, *w, *c, x, *V, r[3];
	int i, j, k;
	lapack_int *ipiv;
	unsigned long int t_buf;

	h = (xN - x1) / N;

	switch(r_type){
		case 0:
			gamma =  mass[0]*mass[1]/(mass[0]+mass[1]);
			break;
		case 1:
			gamma =  mass[0]*mass[2] / (mass[0] + mass[2]);
			break;
		case 2:
			gamma =  mass[2]*mass[1] / (mass[2] + mass[1]);
			break;
		default:
			return 1;
	}
	gamma = 1./478.450668 / gamma;

	a = (double*)calloc((N + 1) * (N + 1), sizeof(double));
	b = (double*)calloc((N + 1) * (N + 1), sizeof(double));
	c = (double*)calloc((N + 1) * (N + 1), sizeof(double));
	w = (double*)calloc((N + 1), sizeof(double));
	V = (double*)calloc((N + 1), sizeof(double));
	ipiv = (lapack_int*)calloc((N + 1), sizeof(lapack_int));

	t_buf = time(NULL);                              // Calculating of current time
	switch(order) {
		case 4:
		// Numerov method for TISE1D 4-th order
			for(i=0; i<N+1; i++){
				a[i*(N+1)+i] = -2./h/h*gamma;
				if(i<N) a[i*(N+1)+i+1] = 1./h/h*gamma;
				if(i>0) a[i*(N+1)+i-1] = 1./h/h*gamma;
			}

			for(i=0; i<N+1; i++){
				b[i*(N+1)+i] = 10./12.;
				if(i<N) b[i*(N+1)+i+1] = 1./12;
				if(i>0) b[i*(N+1)+i-1] = 1./12;
			}
			break;
		case 6:
			// Numerov method for TISE1D 6-th order
				for(i=0; i<N+1; i++){
					a[i*(N+1)+i] = -2.5/h/h*gamma;
					if(i<N) a[i*(N+1)+i+1] = 4./3./h/h*gamma;
					if(i>0) a[i*(N+1)+i-1] = 4./3./h/h*gamma;
					if(i>1) a[i*(N+1)+i-2] = -1./12./h/h*gamma;
					if(i<N-1) a[i*(N+1)+i+2] = -1./12./h/h*gamma;
				}

				for(i=0; i<N+1; i++){
					b[i*(N+1)+i] = 14./15.;
					if(i<N) b[i*(N+1)+i+1] = 2./45.;
					if(i>0) b[i*(N+1)+i-1] = 2./45.;
					if(i>1) b[i*(N+1)+i-2] = -1./90.;
					if(i<N-1) b[i*(N+1)+i+2] = -1./90.;
				}
			break;
		case 8:
			// Numerov method for TISE1D 8-th order
			for(i=0; i<N+1; i++){
				a[i*(N+1)+i] = -49./18./h/h*gamma;
				if(i<N) a[i*(N+1)+i+1] = 1.5/h/h*gamma;
				if(i>0) a[i*(N+1)+i-1] = 1.5/h/h*gamma;
				if(i>1) a[i*(N+1)+i-2] = -3./20./h/h*gamma;
				if(i<N-1) a[i*(N+1)+i+2] = -3./20./h/h*gamma;
				if(i>2) a[i*(N+1)+i-3] = 1./90./h/h*gamma;
				if(i<N-2) a[i*(N+1)+i+3] = 1./90./h/h*gamma;
			}

			for(i=0; i<N+1; i++){
				b[i*(N+1)+i] = 54./56.;
				if(i<N) b[i*(N+1)+i+1] = 15./560.;
				if(i>0) b[i*(N+1)+i-1] = 15./560.;
				if(i>1) b[i*(N+1)+i-2] = -6./560.;
				if(i<N-1) b[i*(N+1)+i+2] = -6./560.;
				if(i>2) b[i*(N+1)+i-3] = 1./560.;
				if(i<N-2) b[i*(N+1)+i+3] = 1./560.;
			}
			break;
		case 10:
			// Numerov method for TISE1D 10-th order
			for(i=0; i<N+1; i++){
				a[i*(N+1)+i] = -14350./5040./h/h*gamma;
				if(i<N) a[i*(N+1)+i+1] = 8064./5040./h/h*gamma;
				if(i>0) a[i*(N+1)+i-1] = 8064./5040./h/h*gamma;
				if(i>1) a[i*(N+1)+i-2] = -1008./5040./h/h*gamma;
				if(i<N-1) a[i*(N+1)+i+2] = -1008./5040./h/h*gamma;
				if(i>2) a[i*(N+1)+i-3] = 128./5040./h/h*gamma;
				if(i<N-2) a[i*(N+1)+i+3] = 128./5040./h/h*gamma;
				if(i>3) a[i*(N+1)+i-4] = -9./5040./h/h*gamma;
				if(i<N-3) a[i*(N+1)+i+4] = -9./5040./h/h*gamma;
			}

			for(i=0; i<N+1; i++){
				b[i*(N+1)+i] = 1. - 70./3150;
				if(i<N) b[i*(N+1)+i+1] = 56./3150.;
				if(i>0) b[i*(N+1)+i-1] = 56./3150.;
				if(i>1) b[i*(N+1)+i-2] = -28./3150.;
				if(i<N-1) b[i*(N+1)+i+2] = -28./3150.;
				if(i>2) b[i*(N+1)+i-3] = 8./3150.;
				if(i<N-2) b[i*(N+1)+i+3] = 8./3150.;
				if(i>3) b[i*(N+1)+i-4] = -1./3150.;
				if(i<N-3) b[i*(N+1)+i+4] = -1./3150.;
			}
			break;
		default:
			// Numerov method for TISE1D 4-th order
			for(i=0; i<N+1; i++){
				a[i*(N+1)+i] = -2./h/h*gamma;
				if(i<N) a[i*(N+1)+i+1] = 1./h/h*gamma;
				if(i>0) a[i*(N+1)+i-1] = 1./h/h*gamma;
			}

			for(i=0; i<N+1; i++){
				b[i*(N+1)+i] = 10./12.;
				if(i<N) b[i*(N+1)+i+1] = 1./12;
				if(i>0) b[i*(N+1)+i-1] = 1./12;
			}
			break;
	}
	
	for(j=0; j<3; j++)
		r[j] = 20.;

	x = x1;
	for(i=0; i<N+1; i++){
		r[r_type] = x;
		V[i] = U_dist(r) + gamma*J*(J+1)/x/x;
		x += h;
	}

	for(i=0; i<N+1; i++){
		//c[i*(N+1)+i] = V(x1) + 1 / (478.450668 * 0.948204436)*J*(J+1);
		//
		c[i*(N+1)+i] = V[i];
	}

//	printf("\n\tMatrixes A and B were filled for %d seconds.\n\n", time(NULL) - t_buf);

//	t_buf = time(NULL);                              // Calculating of current time

	LAPACKE_dgetrf(LAPACK_ROW_MAJOR, N+1, N+1, b, N+1, ipiv);

	LAPACKE_dgetri(LAPACK_ROW_MAJOR, N+1, b, N+1, ipiv);
	
//	printf("\n\tMatrix was inverted for %d seconds.\n\n", time(NULL) - t_buf);

//	printf("\n\tMatrix multiplication.\n\n");
//	t_buf = time(NULL);                              // Calculating of current time

	cblas_dgemm(CblasRowMajor, CblasNoTrans, CblasNoTrans, N+1, N+1, N+1, -1.0, b, N+1, a, N+1, 1.0, c, N+1);
	
//	printf("\n\tIt takes %d seconds.\n\n", time(NULL) - t_buf);

//	t_buf = time(NULL);
//	printf("\n\tCalculating Eigenvalues and Eigenvectors.\n\n");

	LAPACKE_dsyev(LAPACK_ROW_MAJOR, 'N', 'U', N+1, c, N+1, w);

//	printf("\n\tEigenvalues were found for %d seconds.\n\n", time(NULL)-t_buf);
	
	
	for(i=0; i<N+1; i++)
		if(w[i]<D_lim){
			//printf("%.2f ", w[i] * 8.064877E+3);
			//*N_levels++;
		}
		else 
			break;
	*N_levels = i;

	if(*N_levels>0){

		if(r_type==0) {

			if(J==0){
				EvJ_AB.v_max = *N_levels;
				EvJ_AB.Evj = (double**)malloc(EvJ_AB.v_max * sizeof(double*));
				EvJ_AB.Jimax = (int*)calloc(EvJ_AB.v_max, sizeof(int));
				for(i=0; i<EvJ_AB.v_max; i++)
					EvJ_AB.Evj[i] = (double*)calloc((J+1), sizeof(double));
			}

			for(i=0; i<EvJ_AB.v_max; i++){
				if(J!=0)
					EvJ_AB.Evj[i] = (double*)realloc(EvJ_AB.Evj[i], (J+1) * sizeof(double));

				EvJ_AB.Evj[i][J] = w[i];

				EvJ_AB.Jimax[i] = J+1;

			}

		}


	}
	else {
		/*if(r_type==0)
			EvJ_AB.v_max = 0;*/
	}
		

//	printf("\nN=%d", *N_levels);
//	printf("\n");

	free(V);
	free(ipiv);
	free(w);
	free(a);
	free(b);
	free(c);

	return 0;
}

