int deinit_U(){
	int i;

	for(i=0; i<Num_net; i++)
		net[i].delete_net();

	delete[] net;

	return 0;
}

double U_dist(double* r) {

	for(int i=0; i<Num_net; i++)
		if (net[i].is_init() == 0)
			return 0;

	int state, i;
	double input[N_COORDQ], out1, sum = 0.;

	input[0] = exp(-r[2]);
	input[1] = exp(-r[0]) + exp(-r[1]);
	input[2] = exp(-r[0] - r[1]);

	for(i=0; i<Num_net; i++){
		state = net[i].out(input, &out1);

		sum += out1;

		if (state != 0)
			return 0;
	}

	if(Num_net==1)
		return sum;
	else
		return sum*frac_N(Num_net);
}

double U_grad_dist(double* r, double* grad) {
	
	for(int i=0; i<Num_net; i++)
		if (net[i].is_init() == 0)
			return 0;

	int state, i;
	double input[N_COORDQ], out1, gradq1[N_COORDQ], sum;

	input[0] = exp(-r[2]);
	input[1] = exp(-r[0]) + exp(-r[1]);
	input[2] = exp(-r[0] - r[1]);

	for(i=0; i<3; i++)
		grad[i] = 0.;

	for(i=0; i<Num_net; i++){
		state = net[i].grad_input(input, &out1, gradq1);
		if (state != 0)
			return 0;

		sum += out1;

		grad[2] += gradq1[0] * (-input[0]);
		grad[0] += gradq1[1] * (-exp(-r[0])) + gradq1[2] * (-input[2]);
		grad[1] += gradq1[1] * (-exp(-r[1])) + gradq1[2] * (-input[2]);
	}

	if(Num_net==1)
		return sum;
	else {
		for(i=0; i<3; i++)
			grad[i] *= frac_N(Num_net);
		return sum*frac_N(Num_net);
	}
}

double U_grad_hess_dist(double* r, double* grad, double* hess) {
	for(int i=0; i<Num_net; i++)
		if (net[i].is_init() == 0)
			return 0;

	int state, i;
	double input[N_COORDQ], out1, gradq1[N_COORDQ], hessq1[N_COORDQ2], sum = 0.;

	for(i=0; i<3; i++)
		grad[i] = 0.;

	for(i=0; i<N_COORDQ*N_COORDQ; i++)
		hess[i] = 0.;

	input[0] = exp(-r[2]);
	input[1] = exp(-r[0]) + exp(-r[1]);
	input[2] = exp(-r[0] - r[1]);

	//state = net1.hess_grad_input(input, &out1, gradq1, hessq1);

	for(i=0; i<Num_net; i++){
		state = net[i].hess_grad_input(input, &out1, gradq1, hessq1);
		if (state != 0)
			return 0;

		sum += out1;

		grad[2] += gradq1[0] * (-input[0]);
		grad[0] += gradq1[1] * (-exp(-r[0])) + gradq1[2] * (-input[2]);
		grad[1] += gradq1[1] * (-exp(-r[1])) + gradq1[2] * (-input[2]);

			// Diagonal terms (use Wolfram Mathematica)
		hess[0 * N_COORDQ + 0] += input[2] * gradq1[2] + exp(-r[0]) * gradq1[1] + input[2] * input[2] * hessq1[8] + input[2] * exp(-r[0]) * 2 * hessq1[5] + exp(-2. * r[0]) * hessq1[4];
		hess[1 * N_COORDQ + 1] += input[2] * gradq1[2] + exp(-r[1]) * gradq1[1] + input[2] * input[2] * hessq1[8] + input[2] * exp(-r[1]) * 2 * hessq1[5] + exp(-2. * r[1]) * hessq1[4];
		hess[2 * N_COORDQ + 2] += input[0] * gradq1[0] + input[0] * input[0] * hessq1[0];

		// Off-diagonal terms (use Wolfram Mathematica)
		hess[0 * N_COORDQ + 1] += input[2] * gradq1[2] + input[2] * input[2] * hessq1[8] + input[1] * input[2] * hessq1[5] + input[2] * hessq1[4];
		hess[1 * N_COORDQ + 0] += hess[0 * N_COORDQ + 1];
		hess[0 * N_COORDQ + 2] += input[0] * input[2] * hessq1[2] + input[0] * exp(-r[0]) * hessq1[1];
		hess[2 * N_COORDQ + 0] += hess[0 * N_COORDQ + 2];
		hess[1 * N_COORDQ + 2] += input[0] * input[2] * hessq1[2] + input[0] * exp(-r[1]) * hessq1[1];
		hess[2 * N_COORDQ + 1] += hess[1 * N_COORDQ + 2];
	}
	
	if(Num_net==1)
		return sum;
	else {
		for(i=0; i<3; i++)
			grad[i] *= frac_N(Num_net);
		for(i=0; i<9; i++)
			hess[i] *= frac_N(Num_net);
		return sum*frac_N(Num_net);
	}
}

double U_cart(double* r) {
	double* ri, E;
	int i, j, N1, N2, M, N;

	N = N_ATOM;
	M = (N * (N - 1)) / 2;

	ri = (double*)calloc(M, sizeof(double));

	for (i = 0; i < M; i++) {
		IJ(N, i, &N1, &N2);
		ri[i] = 0.;

		for (j = 0; j < 3; j++)
			ri[i] += (r[3 * N1 + j] - r[3 * N2 + j]) * (r[3 * N1 + j] - r[3 * N2 + j]);

		ri[i] = sqrt(ri[i]);
	}

	E = U_dist(ri);

	free(ri);

	return E;
}

double U_grad_cart(double* r, double* grad) {
	double* ri, * gradi, E;
	int i, j, a, N1, N2, M, N;

	N = N_ATOM;
	M = (N * (N - 1)) / 2;

	ri = (double*)calloc(2 * M, sizeof(double));
	gradi = ri + M;

	for (i = 0; i < M; i++) {
		IJ(N, i, &N1, &N2);
		ri[i] = 0.;

		for (j = 0; j < 3; j++)
			ri[i] += sqr(r[3 * N1 + j] - r[3 * N2 + j]);

		ri[i] = sqrt(ri[i]);
	}

	E = U_grad_dist(ri, gradi);

	for (i = 0; i < 3 * N; i++)
		grad[i] = 0.;

	for (j = 0; j < M; j++)
		for (a = 0; a < 3; a++)
			for (i = 0; i < N; i++) {
				int j1;
				j1 = IJK(N, j, i);

				if (j1 >= 0)
					grad[3 * i + a] += gradi[j] * (r[3 * i + a] - r[3 * j1 + a]) / ri[j];
			}

	free(ri);

	return E;
}

double U_grad_hess_cart(double* r, double* grad, double* hess) {
	double* ri, * gradi, * hessi, E = 0, * Dr, * D2r;
	int i, j, k, l, N1, N2, N3, N4, a, b, M, N;

	N = N_ATOM;
	M = (N * (N - 1)) / 2;

	ri = (double*)calloc(2 * M, sizeof(double));
	gradi = ri + M;
	hessi = (double*)calloc(M * M, sizeof(double));
	Dr = (double*)calloc(3 * N * M, sizeof(double));
	D2r = (double*)calloc(3 * N * 3 * N * M, sizeof(double));

	for (i = 0; i < M; i++) {
		IJ(N, i, &N1, &N2);
		ri[i] = 0.;

		for (j = 0; j < 3; j++)
			ri[i] += sqr(r[3 * N1 + j] - r[3 * N2 + j]);

		ri[i] = sqrt(ri[i]);
	}

	for (k = 0; k < M; k++) {
		for (i = 0; i < N; i++)
			for (a = 0; a < 3; a++) {
				int j;
				j = IJK(N, k, i);

				if (j >= 0)
					Dr[3 * N * k + 3 * i + a] = (r[3 * i + a] - r[3 * j + a]) / ri[k];
			}
	}

	for (k = 0; k < M; k++) {
		IJ(N, k, &N1, &N2);
		for (j = 0; j < N; j++)
			for (b = 0; b < 3; b++)
				for (i = 0; i < N; i++)
					for (a = 0; a < 3; a++) {
						if (a == b) {
							double sign = 0.0;
							if (i == j) sign = 1.0;
							else sign = -1.0;
							if ((i == N1) && (j == N2) || (i == N2) && (j == N1) || (i == N1) && (j == N1) || (i == N2) && (j == N2))
								D2r[3 * N * 3 * N * k + 3 * N * (3 * j + b) + 3 * i + a] = sign / ri[k];
						}

						D2r[3 * N * 3 * N * k + 3 * N * (3 * j + b) + 3 * i + a] -= 1. / ri[k] * Dr[3 * N * k + 3 * i + a] * Dr[3 * N * k + 3 * j + b];

					}
	}

	E = U_grad_hess_dist(ri, gradi, hessi);

	for (i = 0; i < 3 * N; i++)
		grad[i] = 0.;

	for (k = 0; k < M; k++)
		for (i = 0; i < 3 * N; i++)
			grad[i] += gradi[k] * Dr[3 * N * k + i];

	for (i = 0; i < 3 * N * 3 * N; i++)
		hess[i] = 0.;

	for (k = 0; k < M; k++)
		for (l = 0; l < M; l++)
			for (i = 0; i < 3 * N; i++)
				for (j = 0; j < 3 * N; j++)
					hess[3 * N * j + i] += hessi[l * M + k] * Dr[3 * N * k + i] * Dr[3 * N * l + j];

	for (k = 0; k < M; k++)
		for (i = 0; i < 3 * N; i++)
			for (j = 0; j < 3 * N; j++)
				hess[3 * N * j + i] += gradi[k] * D2r[3 * N * 3 * N * k + 3 * N * j + i];

	free(Dr);
	free(D2r);
	free(hessi);
	free(ri);

	return E;
}

int IJ(int n, int k, int *i, int *j){
	int a, b = 0, c;
	
	if(n<=1){
		printf("IJ: ERROR IN ATOM AMOUNT (N) - IT SHOULD BE POSITIVE AND MORE THAN 1.\n\n");
		return 1;
	}
	
	if(k<0){
		printf("IJ: ERROR IN DISTANCE NUMBER (K) - IT SHOULD BE NONNEGATIVE INTEGER.\n\n");
		return 2;
	}
	
	if(k>=( n * ( n-1 ) )/2 ){
		printf("IJ: ERROR IN DISTANCE NUMBER (K) - IT SHOULD BE LESS THAN TOTAL DISTANCE AMOUT (N*(N-1)/2).\n\n");
		return 3;
	}

	k++;
	
	for(a=k; a>0; b++){
		n -= 1;
		c = a;
		a -= n;
	}
	
	*i = b-1;
	*j = b+c-1;
	
	return 0;
}

int IJK(int n, int k, int i){
	int N1, N2;
	
	IJ(n, k, &N1, &N2);
	
	if(N1==i) return N2; else
	if(N2==i) return N1; else
	return -1;
}
